
public class ShorthandOp {

	public static void main(String[] args) {
		int a=10,b=a;
		a+=2;
		b-=2;
		
		int r=8*3/2*6+9/4*2-8;
		System.out.println(r);
		r=5/3*9%45*5+9*7/3;
		System.out.println(r);
		r=12%56*9/4%3+90-8%6/2-8;
		System.out.println(r);

		
		// TODO Auto-generated method stub

		System.out.println(a);
		System.out.println(b);
	}

}



	
